import "./App.css";
import { useState, useEffect, useRef } from "react";
import { Chat } from "./components/Chat";
import socket from "./components/Socket";
const Chance = require('chance');
const generate = new Chance();
let users = [];

let message = "";
function App() {
  const [name, setName] = useState("");
  const [userList, setUsers] = useState([]);
  const [toggle, setToggle] = useState(false);

  useEffect(() => {
    let newUser = generate.name();
    setName(newUser);
    socket.emit("newUser", newUser);
  }, []);

  /*Code used to show all the users that they are already connected*/

  useEffect(() => {
    printUsers();
  }, [userList]);

  function printUsers() {
    let div = document.getElementById("GlobalMessage");
    if (toggle) {
      for (var i = 0; i < userList.length; i++) {
        if (userList[i] !== name) {
          div.innerHTML += "<div class='AppUser'>" + userList[i] + "</div>";
        }
      }
    }else{
      div.innerHTML ="";
    }
  }


  function loadUsers() {
    socket.emit("usersConnected");
    socket.on("userList", (array) => {
      users = array;
      setUsers(users);
    });
    setToggle((toggle) => !toggle);
  }

  return (
    <div className="App">
      <div id="Header">
        <div id="UserName">
          {name}
        </div>
        <div id="ViewUsers">
          <button id="ToggleButton" onClick={loadUsers}>Users</button>
        </div>
      </div>
      <div id="GlobalMessage">

      </div>
      <div className="ViewMessages">

      </div>
      <Chat name={name} />
    </div>
  );
}

export default App;
