import React, { useState, useEffect } from 'react';
import socket from './Socket';

// let messageList = [];
export function Messages(props) {
    /*That array saves all current app messages*/
    const [messages, setMessages] = useState([]);

    useEffect(() => {
        socket.on("message_evt", (msgObject) => {
            setMessages([...messages, msgObject]);
            console.log(messages.user);
            console.log(messages.msg);
        })
        return () => { socket.off() };
    }, [messages]);

    function CheckUser(props) {
        // {
        //     messages.map((msg) => {
        //         if (msg.name === props.user) {
        //             return (
        //                 <div className="MyMessageContainer">
        //                     <p>{msg.msg}</p>
        //                 </div>
        //             );
        //         }else{
        //             return(
        //                 <div className="ExternalMessageContainer">
        //                     <p>{msg.msg}</p>
        //                 </div>
        //             )
        //         }
        //     })
        // };
        console.log(messages.user);
        console.log(messages.msg);
        if (props.user !== messages.user) {
            return (
                <div className="ExternalMessageContainer">
                    {messages.map((messages, i) => <div key={i} className="externalMessage">{messages.msg}</div>)}
                </div>
            );
        } else {
            return (
                <div className="MyMessageContainer">
                    {messages.map((messages, i) => <div key={i} className_="myMessage">{messages.msg}</div>)}
                </div>
            )
        }
    }

    return (
        <div>
            <CheckUser user={props.name} />
        </div>
    );


}
