import "./App.css";
import { useState, useEffect, useRef } from "react";
import { Chat } from "./components/Chat";
import socket from "./components/Socket";
const Chance = require('chance');
const generate = new Chance();
let users = [];

let message = "";
function App() {
  const [name, setName] = useState("");
  let [userList, setUsers] = useState([]);

  useEffect(() => {
    let newUser = generate.name();
    setName(newUser);
    socket.emit("newUser", newUser);
    loadUsers();
  }, []);

  function loadUsers(){
    socket.on("userList", (array) =>{
      users = array;
      setUsers(array);
    });
  }

  function showUsers(){
    let div = document.getElementById("GlobalButton");
    div.innerHTML = "";
    for(var i = 0; i < users.length; i++){
      div.innerHTML += "<div>" + userList + "</div>";
    }
  }

  return (
    <div className="App">
      <div id="Header">
        <div id="UserName">
          {name}
        </div>
        <div id="ViewUsers">
          <button id="ToggleButton" onClick={showUsers}>Users</button>
        </div>
      </div>
      <div id="GlobalMessage">

      </div>
      <div className="ViewMessages">

      </div>
      <Chat name={name} />
    </div>
  );
}

export default App;
