import "./App.css";
import { useState, useEffect } from "react";
import socket from "./components/Socket";
import { HomePage } from "./components/HomePage";
const Chance = require('chance');
const generate = new Chance();

function App() {
  const [name, setName] = useState("");
  const [userList, setUsers] = useState([]);

  const [messages, setMessages] = useState([]);
  const [privateMessages, setPrivateMessages] = useState([]);

  let messagesAux = [];
  let privateMessagesAux = [];

  useEffect(() => {
    let isCancelled = false;
    if (!isCancelled) {
      let newUser = generate.name();
      setName(newUser);
      socket.emit("newUser", newUser);
    }
    socket.on("userList", (array) => {
      setUsers(array);
    });
    socket.on("message_evt", (msgObject) => {
      messagesAux.push(msgObject)
      setMessages(messagesAux);
    })
    socket.on("message_evt_private", (msgObject) => {
      privateMessagesAux.push(msgObject)
      setPrivateMessages(privateMessagesAux);
    })
    socket.on("message_evt_private_mine", (msgObject) => {
      privateMessagesAux.push(msgObject)
      setPrivateMessages(privateMessagesAux);
    })
    
    let lastX = 0;
    let lastY = 0;
    let lastZ = 0;

    let lastTime = new Date();

    let shaking = false;
    let timer = null;

    const options = {
      threshold: 15
    };


    if ('Accelerometer' in window) {
      try {
        const acc = new window.Accelerometer({ frequency: 60 });
        /*acc.addEventListener("reading", function(){
    
        })*/
        acc.onreading = () => {
          const deltaX = Math.abs(lastX - acc.x);
          const deltaY = Math.abs(lastY - acc.y);
          const deltaZ = Math.abs(lastZ - acc.z);

          if (((deltaX > options.threshold) && (deltaY > options.threshold)) ||
            ((deltaX > options.threshold) && (deltaZ > options.threshold)) ||
            ((deltaY > options.threshold) && (deltaZ > options.threshold))
          ) {
            if (!shaking) {
              console.log('shake');
              shaking = true;
              document.body.style.backgroundColor = "red";
              if (timer) {
                clearTimeout(timer);
                timer = null;
              }
            }
          } else {
            if (shaking) {
              shaking = false;
              //document.body.style.backgroundColor = "white";
              timer = setTimeout(() => {
                console.log("stop");
                document.body.style.backgroundColor = "white";
              }, 500);
            }
          }

          lastX = acc.x;
          lastY = acc.y;
          lastZ = acc.z;

        }

        acc.start();
      } catch (err) {
        console.log(err);
      }
    }
    return () => isCancelled = true;
  }, []);

  useEffect(() => {
    socket.emit("usersConnected");
  });




  return (
    <div className="App">
      <HomePage name={name} userlist={userList} messages={messages} privateMessages={privateMessages} />
    </div>
  );
}

export default App;
