import React, { useState, useEffect } from "react";
import { GlobalChat } from "./GlobalChat";
import { PrivateChat } from "./PrivateChat";
import socket from "./Socket";
const Chance = require('chance');
const generate = new Chance();

export function HomePage(props) {
    const [name, setName] = useState("");
    const [userList, setUsers] = useState([]);
    //const [globalChat, setGlobalChat] = useState("HomePage");


    useEffect(() => {
        let isCancelled = false;
        if (!isCancelled) {
            let newUser = generate.name();
            setName(newUser);
            socket.emit("newUser", newUser);
        }
        console.log("ENTRA");
        return () => isCancelled = true;
    }, []);

    useEffect(() => {
        socket.emit("usersConnected");
        socket.on("userList", (array) => {
            setUsers(array);
        });
    },[]);

    // function goGlobalChat() {
    //     setGlobalChat("GlobalChat");
    // }
    // function goHomePage() {
    //     setGlobalChat("HomePage");
    // }

    // function goPrivateChat() {
    //     setGlobalChat("PrivateChat");
    // }

    return (

        <div id="HomePageContainer">
            <div id="Header">
                <div id="UserName">
                    {name}
                </div>
                {globalChat !== "HomePage" &&
                    <button onClick={props.newView("HomePage")}>ðŸ¡°</button>
                }
            </div>
            {globalChat === "HomePage" &&
                <div id="HomePageContent">
                    <div id="GlobalChatButton">
                        <button id="GoToGlobalChat" onClick={props.newView("GlobalChat")}>Global Chat</button>
                    </div>
                    {userList.map((user, i) => {
                        if (user.name !== name) {
                            return (
                                <div className="UsersList">
                                    <div className="User">
                                        <button className="PrivateChatButton" onClick={props.newView("PrivateChat")}>{user.name}</button>
                                    </div>
                                </div>
                            )
                        }
                    })}
                </div>
            }
            {globalChat === "GlobalChat" &&

                <GlobalChat name={name} />
            }
            {globalChat === "PrivateChat" &&
                <PrivateChat />
            }

        </div>
    )
}
