import "./App.css";
import { useState, useEffect } from "react";
import { InputMessages } from "./components/InputMessages";
import socket from "./components/Socket";
import { Messages } from "./components/Messages";
import { HomePage } from "./components/HomePage";
import { GlobalChat } from "./components/GlobalChat";

function App() {
  // const [globalChat, setGlobalChat] = useState("HomePage");
  // const [name, setName] = useState("");
  // useEffect(() => {
  //   socket.on("inGlobalChat", (boolean, userName) => {
  //     setGlobalChat(boolean);
  //     setName(userName);
  //   })
  // }, [globalChat])

  // console.log(globalChat);
  // console.log("------------");

  const [globalChat, setGlobalChat] = useState("HomePage");

  const [messages, setMessages] = useState([]);
  useEffect(() => {
    socket.on("message_evt", (msgObject) => {
      setMessages([...messages, msgObject]);
    })
    return () => { socket.off() };
  }, []);

  function changeScreen(newPage){
    setGlobalChat(newPage);
  }

  return (
    <div className="App">
      {/* <HomePage messages={messages} newView={changeScreen}/> */}
      {globalChat === "HomePage" &&
        <HomePage messages={messages} newView={changeScreen}/>
      }
      {globalChat === "GlobalChat" &&
        <GlobalChat name={name} />
      }
      {/* <div id="Header">
        <div id="UserName">
          {name}
        </div>
        <div id="UsersList">

        </div>
        <div id="ViewUsers">
          <button id="ToggleButton" onClick={loadUsers}>Users</button>
        </div>
      </div> */}
      {/* <Messages name={name}/>
      <InputMessages name={name} /> */}
    </div>
  );
}

export default App;
