import React, { useState, useEffect } from 'react';
import socket from './Socket';

export function Messages(props){
    /*That array saves all current app messages*/
    const [messages, setMessages] = useState([]);
    
    socket.on("message_evt", (array) =>{
        setMessages(array);
    });

    useEffect(() =>{
        printMessages();
    }, [messages]);

    function printMessages(){
        let div = document.getElementById("MessageContainer");
        for(var i = 0; i < messages.length; i++){
            if(messages[i].user === props.name){
                div.innerHTML += "<div class='myMessage'>"+ messages[i].msg + "</div>"
            }else{
                div.innerHTML += "<div class='externalMessage'>"+ messages[i].msg + "</div>"
            }
        }
    }
    return(
        <div id="MessageContainer">

        </div>
    );


}
