        //         </div>
        //     );
        // } else {
        //     return (
        //         <div className="MyMessageContainer">
        //             {messages.map((messages, i) => <div key={i} className_="myMessage">{messages.msg}</div>)}
        //         </div>
        //     )
        // }
    }

    return (
        <div>
            <CheckUser user={props.name} />
        </div>
    );


}
